package pattern;

public class Alphabet {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
          for(char ch='a'; ch<='e'; ch++)
          {
        	  for(char ch1='a'; ch1<=ch; ch1++) {
        		  System.out.print(ch1);
        	  }
        	  System.out.println();
          }
          
	}

} 
