package pattern;

public class InvertDiffNum {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
         for(int i=4; i>=1; i--) {
			
			for(int j=i; j>0; j--) {
				
				System.out.print(j);   //syso(i) will be print 4444 333 22 1
				
			}
			System.out.println();
			
		} 

	}

}
/*
4321
321
21
1
  
 */
